import Book from "./Book";

const BooksList = ({
  books,
  title,
  changeCategory,
  read,
  wantToRead,
  currentlyReading,
}) => {
  const getShelf = (book) => {
    read.map((b) => {
      debugger;
      if (b.title === book.title) return "read";
    });

    wantToRead.map((b) => {
      if (b.title === book.title) return "wantToRead";
    });

    currentlyReading.map((b) => {
      if (b.title === book.title) return "currentlyReading";
    });

    return "none";
  };
  return (
    <div className="bookshelf">
      {title && <h2 className="bookshelf-title">{title}</h2>}
      <div className="bookshelf-books">
        <ol className="books-grid">
          {books.map((book) => (
            <li key={book.id}>
              <Book
                book={book}
                changeCategory={changeCategory}
                shelf={
                  read || wantToRead || currentlyReading
                    ? getShelf(book)
                    : book.shelf
                }
              />
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
};

export default BooksList;
