import ShelfChanger from "./ShelfChanger";
import background from "./icons/image_not_found.png";

const Book = ({ book, changeCategory, shelf }) => {
  // useEffect(() => {
  //   return () => {
  //     console.log("Kiro");
  //   };
  // }, []);

  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: "100%",
            height: "100%",
            backgroundSize: 400,
            backgroundPosition: "bottom",
            backgroundRepeat: "no-repeat",
            backgroundImage: `url(${
              book.imageLinks ? book.imageLinks.thumbnail : background
            })`,
          }}
        ></div>
        <ShelfChanger
          book={book}
          changeCategory={changeCategory}
          shelf={shelf}
        />
      </div>
      <div className="book-title">{book.title}</div>
      <div>
        {book.authors
          ? book.authors.map((author, index) => (
              <span key={author} className="book-authors">
                {author}
                {index + 1 < book.authors.length ? ", " : ""}
              </span>
            ))
          : ""}
      </div>
    </div>
  );
};

export default Book;
