import { Link } from "react-router-dom";
import { useState } from "react";
import * as BooksAPI from "./BooksAPI";
import Book from "./Book";

const Search = ({ getBooks, changeCategory }) => {
  const [result, setResult] = useState([]);

  const getSearchResult = async (search) => {
    console.log(search);
    console.log(performance.now());
    const res = await BooksAPI.search(search, 10);
    console.log(performance.now());

    console.log(res);
    return res;
  };

  const searchBooks = (search) => {
    getSearchResult(search).then((res) => {
      setResult(res);
    });
  };

  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link className="close-search" to="/">
          Close
        </Link>

        <div className="search-books-input-wrapper">
          <input
            type="text"
            onChange={(e) => {
              searchBooks(e.target.value);
            }}
            placeholder="Search by title, author, or ISBN"
          />
        </div>
      </div>

      <div className="search-books-results">
        <ol className="books-grid">
          {typeof result === "array" &&
            result.map((book) => {
              return (
                <li key={book.id}>
                  <Book book={book} changeCategory={changeCategory} />
                </li>
              );
            })}
        </ol>
      </div>
    </div>
  );
};

export default Search;
